import { Component, ElementRef, Injectable, Input, OnInit, ViewChild } from '@angular/core';
import { mangueira } from '../models/mangueira';
import { valvula } from '../models/valvula';
import { MangueiraService } from '../services/mangueira.service';
import { ValvulaService } from '../services/valvula.service';
import jspdf, { jsPDF } from 'jspdf';
import html2canvas from 'html2canvas';
@Component({
  selector: 'app-tela-relatorio',
  templateUrl: './tela-relatorio.component.html',
  styleUrls: ['./tela-relatorio.component.scss']
})

export class TelaConfigComponent implements OnInit {
  @Input('planilha') public planilha: any;
  public xmlHttp = new XMLHttpRequest();
  public detalhes: boolean = true;
  public detalhesId!: number;

  constructor() {
    this.dao();
  }

  ngOnInit() { }

  dao() {
    //CONEXAO COM A API
    async function getPlanilha() {
      const response = await fetch(`http://localhost:5432/SA-Bombeiros`);
      return await response.json();
    }

    //promisses
    getPlanilha()
      .then((planilha) => this.planilha = planilha)


  }

  detalhesProd(id: number) {
    this.detalhesId = id;
    this.detalhes = !this.detalhes;
    console.log(this.detalhesId)
  }

}
