import { bombeiro } from './../models/bombeiro';
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from 'src/environments/environment';

@Injectable({
  providedIn: 'root'
})
export class BombeirosService {
  url: string = environment.apiUrl + '/bombeiro';

  constructor(private http: HttpClient) { }
  async create(bombeiro: bombeiro) {
    return this.http
      .post(this.url, bombeiro)
      .toPromise();
  }
  async read(id: number) {
    return this.http.get(this.url+ '/' + id).toPromise();
  }
  async update(bombeiro: bombeiro) {
    return this.http
      .put(this.url + '/' + bombeiro.id, bombeiro)
      .toPromise()
  }
  async delete(id: number) {
    this.http.delete(this.url + '/' + id).toPromise();
  }

  async list() {
    return this.http.get(this.url).toPromise();
  }



}
