import { Component, OnInit} from '@angular/core';
import { Router } from '@angular/router';
import 'ol/ol.css';
import Map from 'ol/Map';
import View from 'ol/View';
import {XYZ, Cluster,  Vector as VectorSource} from 'ol/source';
import {Tile as TileLayer, Vector as VectorLayer} from 'ol/layer';
import * as Proj from 'ol/proj.js';//Converter cordenadas para lat e long
import { DadosService } from '../services/dados.service';
import 'ol/ol.css';
import Feature from 'ol/Feature';
import Point from 'ol/geom/Point';
import {
  Circle as CircleStyle,
  Fill,
  Stroke,
  Style,
  Text,
} from 'ol/style';
import {boundingExtent} from 'ol/extent';

@Component({
  selector: 'app-tela-home',
  templateUrl: './tela-home.component.html',
  styleUrls: ['./tela-home.component.scss']
})

export class TelaHomeComponent implements OnInit {

  constructor(private router: Router,
     private dadosService:DadosService) { }
  pontos: any;

   ngOnInit() {}

  async ngAfterViewInit(){

    this.pontos = await this.dadosService.list();
    const map = this.MapaCreate();
    const cluster = this.cluster();
    map.addLayer(cluster);
    this.cliqueEventMapa(map, cluster);
  }


  private cliqueEventMapa(map: Map, cluster: any) {
    map.on('click', (evento) => {
      cluster.getFeatures(evento.pixel).then((clickedFeatures: any) => {
        if (clickedFeatures.length) {
          const features = clickedFeatures[0].get('features');
          if (features.length > 1) {
            const extent = boundingExtent(
              features.map((r: any) => r.getGeometry().getCoordinates())
            );
            map
              .getView()
              .fit(extent, { duration: 1000, padding: [50, 50, 50, 50] });
          }
        }
      });
    });
  }


  private MapaCreate(){

    const latitude = -48.613666;
    const longitude = -27.595115

    const raster = new TileLayer({
      source: new XYZ({
        url: 'https://mt1.google.com/vt/lyrs=r&x={x}&y={y}&z={z}',
      }),
    });

    const map = new Map({
      layers: [raster],
      target: 'map',
      view: new View({
        center: Proj.fromLonLat([latitude, longitude]),
        zoom: 14,
      }),
    });
    return map;
  }




  private cluster(){
    const features: any[] = [];
    for (let i = 0; i < this.pontos.length; ++i) {
      const ponto = Proj.fromLonLat([
        this.pontos[i].longitude,
        this.pontos[i].latitude,
      ]);
      features[i] = new Feature(new Point(ponto));
    }

    const source = new VectorSource({
      features: features,
    });

    const clusterSource = new Cluster({
      distance: 80,
      source: source,
    });

    const styleCache: any = {};

    const clusters = new VectorLayer({
      source: clusterSource,
      style: function (feature) {
        const size = feature.get('features').length;
        let style = styleCache[size];
        if (!style) {
          style = new Style({
            image: new CircleStyle({
              radius: 14,
              stroke: new Stroke({
                color: '#ff0000',
              }),
              fill: new Fill({
                color: '#ff0000',
              }),
            }),
            text: new Text({
              text: size.toString(),
              fill: new Fill({
                color: '#fff',
              }),
              scale: 1.3,
            }),
          });
          styleCache[size] = style;
        }
        return style;
      },
    });

    return clusters;
  }



  showFiller = false;



   telaConfigs(): void {

    let alterado = false;
    this.router.navigate(['/telaHome/telaConfig']); 
    alterado = true;

    if(alterado = true){
      this.router.navigate(['/telaHome/']); alterado = false;
    }

    }


  }  
<div class="container">
  <mat-drawer-container autosize>

    <mat-toolbar class="CampoSuperior" autosize>

      <img src="https://cdn-icons-png.flaticon.com/512/1/1324.png"
        style="width: 64px; height: 64px; padding: 10px; filter: invert(80%)" />
      <span class="Titulo01">Bombeiros Digital</span>

      <button type="button" class="btnLateralMenu" mat-button (click)="drawer.toggle()">
        Ferramentas
      </button>



    </mat-toolbar>
    <!-- ↓ Menu lateral ↓ -->
    <mat-drawer #drawer class="sidenav" mode="side">
      <h2>Menu Principal</h2>
      <br />

      <p class="linker" routerLink="telaRelatorio" >
        <img class="imgTool" src="/assets/REL.png" />Relatórios</p>

      <br />

      <p class="linker" (click)="config.toggle()" >
        <img class="imgTool" src="/assets/config.png" />Configurações</p>

      <br />
      <p class="linker" routerLink="">
        <img class="imgTool" src="/assets/sair.png" />Sair
      </p>
    </mat-drawer>
      <!-- ↓ centro da tela home ↓ -->
      <div class="flex-container">

        <div class="mapaEntrada">

          <!-- zona do mapa -->
          <div id="map" class="mapa01"></div>


        </div>
      </div>
  </mat-drawer-container>

    <mat-drawer #config class="sidenav" mode="side" autosize>
      <!--<app-tela-config></app-tela-config>-->
      <p   class="linker" (click)="telaConfigs()" >
        Cadastro de usuario
      <p>
        <p class="linker" (click)="config.toggle()" >>>Voltar</p>
    </mat-drawer>


    <br />
    <br/>


</div>
