import { Component, Injectable, Input, OnInit } from '@angular/core';
import { mangueira } from '../models/mangueira';
import { valvula } from '../models/valvula';
import { MangueiraService } from '../services/mangueira.service';
import { ValvulaService } from '../services/valvula.service';
import * as jspdf from 'jspdf';
import html2canvas from 'html2canvas';

@Component({
  selector: 'app-tela-relatorio',
  templateUrl: './tela-relatorio.component.html',
  styleUrls: ['./tela-relatorio.component.scss']
})





export class TelaConfigComponent implements OnInit{ 

  @Input('planilha') public planilha: any;
  public xmlHttp = new XMLHttpRequest();
  public detalhes: boolean = true
  public detalhesId: number | undefined;

  mangueiras: mangueira[] = [];
  valvulas: valvula[] = [];

  constructor(private valvulaService:ValvulaService, private mangueiraService:MangueiraService) { this.dao();}

  async ngOnInit(): Promise<void> {

      this.valvulas = await this.valvulaService.list();
     this.mangueiras = await this.mangueiraService.list();    

    }


  ngOnDestroy(){

  }


   public deletaProd(id: any){
    function reqListener () {
      console.log('tentou excluir ',id)

    };

    var oReq = new XMLHttpRequest();
    oReq.onload = reqListener;
    oReq.open("DELETE", "http://localhost:8081/"+id+"", true);
    oReq.send();
    this.dao();
    this.load();
  }


  public detalhesProd(id: any){
    this.detalhesId = id;
    this.detalhes = !this.detalhes;
    console.log(this.detalhesId)
  }

  dao(){
//CONEXAO COM A API
async function getPlanilha()
{
  const response = await fetch(`http://localhost:8081/`);
  return await response.json();
}

//promisses
getPlanilha()
  .then((planilha) => this.planilha = planilha)
}

load() {
  //Session storage salva os dados como string

  (sessionStorage.refresh == 'true' || !sessionStorage.refresh) && location.reload();
  sessionStorage.refresh = true;

}

public downloadAsPDF() {
  const doc = new jsPDF();

  const specialElementHandlers = {
    '#editor': function (element, renderer) {
      return true;
    }
  };

  const pdfTable = this.pdfTable.nativeElement;

  doc.fromHTML(pdfTable.innerHTML, 15, 15, {
    width: 190,
    'elementHandlers': specialElementHandlers
  });

  doc.save('tableToPdf.pdf');
}

exportAsPDF(divId)
    {
        let data = document.getElementById('divId');  
        html2canvas(data).then(canvas => {
        const contentDataURL = canvas.toDataURL('image/png')  
        let pdf = new jspdf('l', 'cm', 'a4'); //Generates PDF in landscape mode
        // let pdf = new jspdf('p', 'cm', 'a4'); Generates PDF in portrait mode
        pdf.addImage(contentDataURL, 'PNG', 0, 0, 29.7, 21.0);  
        pdf.save('Filename.pdf');   
      }); 
    }
}
