import { Component, Injectable, OnInit } from '@angular/core';
import jsPDF from 'jspdf';
import { mangueira } from '../models/mangueira';
import { valvula } from '../models/valvula';
import { MangueiraService } from '../services/mangueira.service';
import { ValvulaService } from '../services/valvula.service';


declare var require: any;
const FileSaver = require('file-saver');

@Component({
  selector: 'app-tela-relatorio',
  templateUrl: './tela-relatorio.component.html',
  styleUrls: ['./tela-relatorio.component.scss']
})

@Injectable()
export class TelaConfigComponent implements OnInit {
  mangueiras: mangueira[] = [];
  valvulas: valvula[] = [];

  constructor(
    private vService: ValvulaService,
    private mService: MangueiraService
  ) { }

  async ngOnInit(): Promise<void> {
    this.mangueiras = (await this.mService.list()) as mangueira[];
    this.valvulas = (await this.vService.list()) as valvula[];
  }

  // baixarPDF() {
  //   const pdUrl = 'assets/Projetos';
  //   const pdfName = 'Projetos.pdf';
  //   FileSaver.saveAs(pdUrl, pdfName);
  // }


  downloadPDF() {

    var doc = new jsPDF();
   // var imgData = 'assets/corpoDeBombeiros.png'

    doc.setFontSize(40);
    doc.text('Relatório' + Date.now, 75, 25);
   // doc.addImage(imgData, 'PNG', 15, 40, 100, 160);

    doc.setFillColor(50, 50, 50);
    doc.rect(10, 20, 30, 8, "FD");
    doc.rect(10, 28, 30, 8, "FD");
    doc.rect(10, 36, 30, 8, "FD");
    doc.rect(10, 44, 30, 8, "FD");

    doc.setFontSize(12);
    doc.setTextColor(255, 255, 255);
    doc.text("Id", 12, 25);
    doc.text("Tipo", 12, 33);
    doc.text("Validade", 12, 41);
    doc.text("Usuario", 12, 48);


  doc.setFont("normal");
  doc.setTextColor(0, 0, 0);
  doc.text(`${this.mService.list()}`, 42, 25);
  doc.text(`${this.vService.list()}`, 42, 33);

    console.log("bomdia");
    doc.output("dataurlnewwindow");
    //doc.save("Relatório.pdf");
  }



}
