import { Component, OnInit} from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute ,Router } from '@angular/router';
import { BombeirosService } from '../services/bombeiro.service';
import 'ol/ol.css';
import 'ol/ol.css';
import { bombeiro } from '../models/bombeiro';
import { MatSnackBar } from '@angular/material/snack-bar';
import { AuthService } from '../auth/auth.service';

@Component({
  selector: 'app-tela-home',
  templateUrl: './tela-cadastro.component.html',
  styleUrls: ['./tela-cadastro.component.scss']
})

export class TelaCadastroComponent implements OnInit {

  bombeiros: bombeiro[] = []

  constructor(private route: ActivatedRoute, private authService: AuthService, 
    private router: Router, private bombeiroService: BombeirosService, 
    private snackbar: MatSnackBar) { }

  subscription: any;

  edit = false;


 async  ngOnInit(): Promise<void> {

    this.bombeiros = (await this.bombeiroService.list()) as bombeiro[]

  }
  bombeiro?: bombeiro;




  cadastroForm = new FormGroup({

    nome: new FormControl('', [Validators.required]),
    senha: new FormControl('', [Validators.required]),
    nomeDeGuerra: new FormControl(''),
    matricula: new FormControl('', [Validators.required]),
    perfilAcesso: new FormControl(''),
    email: new FormControl('', [Validators.required]),

  });

  private async update(){
    try{
      const bombeiroUp = await this.bombeiroService.update(this.bombeiro!.id!, this.bombeiro!);
      this.snackbar.open('Bombeiro atualizado com sucesso', 'x',{
        horizontalPosition: 'center',
        verticalPosition: 'bottom',
        panelClass: 'success'

      })
      return bombeiroUp;
    }catch(error){
      this.snackbar.open('Não foi possivel atualizar o bombeiro', 'x', {
        horizontalPosition: 'center',
        verticalPosition: 'top',
        panelClass: 'warn',
      })
      throw error;
    }
  }

   async enviar(){
     await this.bombeiroService.create(this.cadastroForm.value);
      this.cadastroForm.reset();
     this.snackbar.open('Bombeiro criado com sucesso', 'x', {
             horizontalPosition: 'center',
             verticalPosition: 'bottom',
             panelClass: 'success'
           })
   }






  }
<div class="flex-container">
<mat-tab-group mat-stretch-tabs mat-align-tabs="center" class="tabsGroup" >


  <mat-tab label="Gerenciamento de Bombeiros"  class="tabsLayer mat-tab">

      <h2>Gerenciamento de Bombeiros</h2>
      <form [formGroup]="cadastroForm" style="margin-left: 4px;" class="formCad">
        <input  class="inputs" type="email" placeholder="Email" id="emailid" formControlName="email"/><br />
        <input class="inputs"type="text" placeholder="Nome"  id="nomeid" formControlName="nome"/><br />
        <input class="inputs" type="password" placeholder="Senha" id="senhaid" formControlName="senha"/><br />
        <input class="inputs" type="number" placeholder="Matricula"  id="matriculaid" formControlName="matricula"/><br />
        <input class="inputs" type="text" placeholder="Nome de Guerra" id="nomeDeGuerraid" formControlName="nomeDeGuerra"/><br />
        <input class="inputs" type="text" placeholder="Perfil de acesso" id="perfildeAcessoid" formControlName="perfilAcesso"/><br />

        <input type="button" (click)="enviar()" value="Cadastrar"  id="submit01">
      </form>

      <div id="campoTable">

        <table>



            <tr>
              <td>ID</td>
              <td>Email</td>
              <td>matricula</td>
              <td>nome</td>
              <td>Nome de guerra</td>
              <td>Perfil de Acesso</td>
            </tr>


          <tr *ngFor="let b of bombeiros" >
            <td id="id01"> {{b.id }}</td>
            <td>{{b.nome}}</td>
            <td>{{b.email}}</td>
            <td>{{b.matricula}}</td>
            <td>{{b.perfilAcesso}}</td>
            <td>{{b.nomeDeGuerra}}</td>


          </tr>
        </table>




    </div>




  </mat-tab>
  <mat-tab label="Editar" class="tabsLayer">Content 2</mat-tab>
  <mat-tab label="Bombeiros Cadastrados" class="tabsLayer">Content 3</mat-tab>
</mat-tab-group>






</div>
