import { Component, OnInit} from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute ,Router } from '@angular/router';
import { BombeirosService } from '../services/bombeiro.service';
import 'ol/ol.css';
import 'ol/ol.css';
import { bombeiro } from '../models/bombeiro';
import { MatSnackBar } from '@angular/material/snack-bar';
import { AuthService } from '../auth/auth.service';

@Component({
  selector: 'app-tela-home',
  templateUrl: './tela-cadastro.component.html',
  styleUrls: ['./tela-cadastro.component.scss']
})

export class TelaCadastroComponent implements OnInit {
  edit = false;
  bombeiro?: bombeiro;

  constructor(private route: ActivatedRoute, private authService: AuthService, private router: Router, private bombeiroService: BombeirosService, private snackbar: MatSnackBar) { }

  subscription: any;

  ngOnInit(): void {
    this.subscription = this.route.url.subscribe((url) => {
    if(url.toString().includes('me')) {
      this.bombeiro = this.authService.getUser();
      this.update()
      this.edit = true;
    }

    })


  }


  updateForm() {
    this.cadastroForm.setValue({
      nome: this.bombeiro!.nome || '',
      email: this.bombeiro!.email || '',
      senha: this.bombeiro!.senha || '',
      matricula: this.bombeiro?.matricula || '',
      nomeDeGuerra: this.bombeiro!.nomeDeGuerra || '',
      perfilAcesso: this.bombeiro!.perfilAcesso || '',
    });
  }


  cadastroForm = new FormGroup({

    nome: new FormControl('', [Validators.required]),
    senha: new FormControl('', [Validators.required]),
    nomeDeGuerra: new FormControl(''),
    matricula: new FormControl('', [Validators.required]),
    perfilAcesso: new FormControl(''),
    email: new FormControl('', [Validators.required]),

  });






  getBombeiroForm(){
    if(!this.bombeiro) this.bombeiro = {} as bombeiro;

    this.bombeiro!.nome = this.cadastroForm.value.nome;
    this.bombeiro!.email = this.cadastroForm.value.email;
    this.bombeiro!.senha = this.cadastroForm.value.senha;
    this.bombeiro!.perfilAcesso = this.cadastroForm.value.perfilAcesso;
    this.bombeiro!.matricula = this.cadastroForm.value.matricula;
    this.bombeiro!.nomeDeGuerra = this.cadastroForm.value.nomeDeGuerra;

  }

  private async update(){
    try{
      const bombeiroUp = await this.bombeiroService.update(this.bombeiro!.id!, this.bombeiro!);
      this.snackbar.open('Bombeiro atualizado com sucesso', 'x',{
        horizontalPosition: 'center',
        verticalPosition: 'bottom',
        panelClass: 'success'

      })
      return bombeiroUp;
    }catch(error){
      this.snackbar.open('Não foi possivel atualizar o bombeiro', 'x', {
        horizontalPosition: 'center',
        verticalPosition: 'top',
        panelClass: 'warn',
      })
      throw error;
    }
  }

   async enviar(){
     await this.bombeiroService.create(this.cadastroForm.value);
      this.cadastroForm.reset();
     this.snackbar.open('Bombeiro criado com sucesso', 'x', {
             horizontalPosition: 'center',
             verticalPosition: 'bottom',
             panelClass: 'success'
           })
   }

