import { Component, OnInit} from '@angular/core';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Router } from '@angular/router';
import { AuthService } from '../auth/auth.service';
import { BombeirosService } from '../services/bombeiro.service';



@Component({
  selector: 'app-tela-login',
  templateUrl: './tela-login.component.html',
  styleUrls: ['./tela-login.component.scss'],
})


export class TelaLoginComponent implements OnInit {

  // Variaveis dos inputs
  matricula: string = "";
  senha: string = "";

  //Ativador do Alert de erro
  isAlert1 : boolean = false;

  constructor(private authService: AuthService ,private router: Router, private bombeiroService: BombeirosService, private snackbar: MatSnackBar) {
  }


async ngOnInit() {
    const token = this.authService.getStoredToken();
    if(token && this.authService.isLogged()){
      this.router.navigate(['home']);
    }
  }



  async entrar() {

    const newTokenRequest: any = {
      matricula: this.matricula,
      senha: this.senha,
    };

    await this.authService.getUser();
    await this.authService.createToken(newTokenRequest);

    this.router.navigate(['home']);


    if(this.authService.getUser().senha != this.senha || this.authService.getUser().matricula != this.matricula)
   {
    this.snackbar.open('Campos inválidos!', 'x', {
      horizontalPosition: 'center',
      verticalPosition: 'bottom',
      panelClass: 'warn',  })
   } 


     }


     public async logar(){

      const newTokenRequest: any = {
        matricula: this.matricula,
        senha: this.senha,
      };

      try{

        await this.authService.createToken(newTokenRequest);

        await this.authService.getUser();
        this.router.navigate(['home/mapa']);

      }catch (error){


        throw error;
      }
    }


}
<!-- ↓Fundo do site↓ -->
<div class="container">

  <!-- ↓Caixa de login onde estão os campos↓ -->
  <div class="campoLogin">

    <!-- ↓Titulo do campo de login↓ -->
    <div class="topoLogin">
      <label class="labelLogin01">Tela Login</label>
    </div>

    <!-- ↓Campos de login↓ -->
    <form>

        <label for="campName" class="labelsLogin">Matricula:</label>
        <input [(ngModel)]="matricula" type="text" placeholder="00000" 
          class="campName entrada"
          name="username"
        />


        <label for="campPassword" class="labelsLogin">Senha:</label>
        <input
        [(ngModel)]="senha"
          type="password"
          placeholder="******"
          class="campPassword entrada"
          name="password"
        />

      <button class="btnEntrar buttonsLogin" data-dismiss="alert" (click)="entrar()" >Entrar</button>
    </form>
  </div>

<!-- ↓ Alerta de erro para a tela ↓  -->
  <div *ngIf="isAlert1" style="margin-top: 47em; position: fixed;">
    <p>
      <ngb-alert type="danger" [dismissible]="true">
        <strong>Atenção!</strong> Email ou senha estão errados.
      </ngb-alert>
    </p>
  </div>

  <!-- <div *ngIf="this.name == '' || this.password == ''" class="alert alert-danger" role="alert">Erro! Senha invalida</div> -->
</div>


<router-outlet></router-outlet>
