import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import 'ol/ol.css';
import Map from 'ol/Map';
import View from 'ol/View';
import {XYZ} from 'ol/source';
import {Tile as TileLayer} from 'ol/layer';
import * as Proj from 'ol/proj.js';//Converter cordenadas para lat e long
import { DadosService } from '../services/dados.service';


@Component({
  selector: 'app-tela-home',
  templateUrl: './tela-home.component.html',
  styleUrls: ['./tela-home.component.scss']
})



export class TelaHomeComponent implements OnInit {

  constructor(private router: Router, private dadosService:DadosService) { }
  pontos: any;

  async ngOnInit() {
    this.pontos = await this.dadosService.list();
    console.log(this.pontos);
  }

  ngAfterViewInit(){

   const map = this.criarMapa();
  }

  private criarMapa(){

    const longitude = -48.613666;
    const latitude = -27.595115;

    const raster = new TileLayer({
      source: new XYZ({
        url: 'https://mt1.google.com/vt/lyrs=r&x={x}&y={y}&z={z}&apistyle=s.t%3A1%7Cp.v%3Aon%2Cs.t%3A21%7Cs.e%3Al%7Cp.v%3Aoff%2Cs.t%3A2%7Cp.v%3Aoff%2Cs.t%3A2%7Cs.e%3Al.t%7Cp.v%3Aoff%2Cs.t%3A33%7Cp.v%3Aoff%2Cs.t%3A33%7Cs.e%3Al.i%7Cp.v%3Aoff%2Cs.t%3A3%7Cs.e%3Al.i%7Cp.v%3Aoff%2Cs.t%3A51%7Cs.e%3Al%7Cp.v%3Aoff%2Cs.t%3A4%7Cp.v%3Aoff',

      }),
    });

    const map = new Map({
      layers: [raster],
      target: 'map',
      view: new View({
        center: Proj.fromLonLat([longitude, latitude]),
        zoom: 14,
      }),
    });
  }

  private cluster(){

  }


  showFiller = false;



   telaConfigs(): void {

    let alterado = false;
    this.router.navigate(['/telaHome/telaConfig']); 
    alterado = true;

    if(alterado = true){
      this.router.navigate(['/telaHome/']); alterado = false;
    }

    }


  }  
<div class="container">
  <mat-drawer-container autosize>

    <mat-toolbar class="CampoSuperior" autosize>

      <img src="https://cdn-icons-png.flaticon.com/512/1/1324.png"
        style="width: 64px; height: 64px; padding: 10px; filter: invert(80%)" />
      <span class="Titulo01">Bombeiros Digital</span>

      <button type="button" class="btnLateralMenu" mat-button (click)="drawer.toggle()">
        Ferramentas
      </button>
    </mat-toolbar>
    <!-- ↓ Menu lateral ↓ -->
    <mat-drawer #drawer class="sidenav" mode="side">
      <h2>Menu Principal</h2>
      <br />

      <br />

      <p class="linker" (click)="config.toggle()" >
        <img class="imgTool" src="/assets/config.png" />Configurações</p>

      <br />
      <p class="linker" routerLink="">
        <img class="imgTool" src="/assets/sair.png" />Sair
      </p>
    </mat-drawer>

    <mat-drawer #config class="sidenav" mode="side">
      <!--<app-tela-config></app-tela-config>-->
      <p   class="linker" (click)="telaConfigs()" >
        Cadastro de usuario
      <p>
    </mat-drawer>

    <br />
    <br />
            <!-- ↓ centro da tela home ↓ -->
    <div class="flex-container">

      <div class="mapaEntrada">

        <!-- zona do mapa -->
        <div id="map" class="mapa01"></div>

      </div>
    </div>
  </mat-drawer-container>
</div>
